mapper.select:
SELECT post_id as postId, title, content, category, admin_id as adminId, created_at as createdAt, updated_at as updatedAt, status, fix, link
FROM post
WHERE category = #{category}
ORDER BY created_at DESC;

SELECT post_id as postId, title, content, category, admin_id as adminId, created_at as createdAt, updated_at as updatedAt, status, fix, link
FROM post
WHERE post_id = #{postId};

mapper.update:
UPDATE post
SET title = #{title}, content = #{content}, status = #{status}, fix = #{fix}, link = #{link}
WHERE post_id = #{postId};

UPDATE post
SET status = #{status}
WHERE post_id = #{postId};

mapper.select:
SELECT fix
FROM post
WHERE post_id = #{postId};

mapper.update:
UPDATE post
SET fix = #{fix}
WHERE post_id = #{postId};

mapper.select:
SELECT status
FROM post
WHERE post_id = #{postId};
