mapper.select:
SELECT p.post_id as postId, p.title, p.content, p.category, p.admin_id as adminId, p.created_at as createdAt, p.updated_at as updatedAt, p.status, p.fix, p.link, pi.file_name as imageUrl
FROM post p
LEFT JOIN postimages pi ON p.post_id = pi.post_id
WHERE p.category = #{category}
ORDER BY p.created_at DESC
LIMIT #{size} OFFSET #{offset};

SELECT COUNT(*)
FROM post
WHERE category = #{category};

mapper.update:
UPDATE post
SET title = #{title}, content = #{content}, link = #{link}
WHERE post_id = #{postId};

UPDATE post
SET status = #{status}
WHERE post_id = #{postId};

mapper.select:
SELECT fix
FROM post
WHERE post_id = #{postId};

mapper.update:
UPDATE post
SET fix = #{fix}
WHERE post_id = #{postId};

mapper.select:
SELECT status
FROM post
WHERE post_id = #{postId};
