mapper.select:
SELECT post_id, title, content, category, admin_id, created_at, updated_at, status, fix, link, image_url
FROM post
WHERE category = #{category}
ORDER BY created_at DESC
LIMIT 3;

mapper.update:
UPDATE post
SET title = #{title}, content = #{content}, status = #{status}, fix = #{fix}, link = #{link}, image_url = #{image_url}
WHERE post_id = #{post_id};

UPDATE post
SET status = #{status}
WHERE post_id = #{post_id};

mapper.select:
SELECT fix
FROM post
WHERE post_id = #{post_id};

mapper.update:
UPDATE post
SET fix = #{fix}
WHERE post_id = #{post_id};

mapper.select:
